    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        //Obtener datos vista y guardarlos en arraylist
        String nombre = nom.getText();
        String raza =raz.getText();
        String color =col.getText();
        int edad = Integer.parseInt(col.getText());
        String t =tipo.getSelectedItem().toString();
        if (t.equals("Felino")) {
            String tip =toxo.getText();            
            Mascota.mascotas.add(new Felinos(tip, nombre, raza, color, edad));
        }else if(t.equals("Canino")){
            String tip = ent.getText();
            Mascota.mascotas.add(new Caninos(tip, nombre, raza, color, edad));
                }
    }//GEN-LAST:event_jButton1ActionPerformed

