    private void tipoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tipoActionPerformed
        String valor= (String) tipo.getSelectedItem();
        if (valor.equals("Felino")) {
            nivelTxt.setVisible(false);
            ent.setVisible(false);
            libreTxt.setVisible(true);
            toxo.setVisible(true); 
        } else {
            nivelTxt.setVisible(true);
            ent.setVisible(true);
            libreTxt.setVisible(false);
            toxo.setVisible(false);
        }
    }//GEN-LAST:event_tipoActionPerformed

